import './prompt.css';

const Prompt = () => {
    const [prompt, setPrompt] = useState('');
    const [image, setImage] = useState('');  // State variable for the image data

    const handleSubmit = (event: React.FormEvent) => {
        event.preventDefault();
        console.log(prompt);
        // TODO: Add the logic to generate the image data based on the prompt
        // For the demonstration, let's assume the data is obtained and stored in a variable imageData
        // setImage(imageData);
    };

    const handleVerify = () => {
        console.log('Verification started.');
        // TODO: Add your verification logic here
    };

    return (
        <div className="prompt-container">
            <h2 className="prompt-label">Prompt:</h2> {/* Moved up and added class */}
            <form onSubmit={handleSubmit}>
                <textarea value={prompt} onChange={e => setPrompt(e.target.value)} />
                <button type="submit">Enter</button>
            </form>
            <div className="image-box">
                {image && <img src={`data:image/png;base64,${image}`} alt="Generated" />}
            </div>
            <button onClick={handleVerify}>Verify</button>
        </div>
    );
};

export default Prompt;
