import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import './Prompt.css';
import { fileSave } from 'browser-fs-access';

const Prompt = () => {
    const [prompt, setPrompt] = useState('');
    const [image, setImage] = useState('');

    const handlePromptChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setPrompt(event.target.value);
    };

    const handleEnter = async () => {
        // Create a JSON object from the prompt
        const jsonObject = { prompt };

        // Convert the JSON object to a string
        const jsonString = JSON.stringify(jsonObject, null, 2);

        // Save the JSON string to a file
        await fileSave(
            new Blob([jsonString], { type: 'application/json' }),
            { fileName: 'prompt.json' }
        );

        // TODO: send the prompt to your server and get the base64 image data

        // Let's say this is the received base64 image data
        const receivedImageData = 'iVBORw0KG...'; // replace this with your actual data

        // Set the received image data
        setImage(receivedImageData);
    };

    return (
        <div className="prompt-container">
            <h2>Prompt</h2>
            <textarea value={prompt} onChange={handlePromptChange} />
            <Button variant="contained" color="primary" onClick={handleEnter}>Enter</Button>
            <h2>Generated Image</h2>
            {image && <img src={`data:image/png;base64,${image}`} alt="Generated" />}
        </div>
    );
};

export default Prompt;
